import React from 'react';
import { Button, Flex, Switch, Tooltip, Typography  } from 'antd';
import "../images/logo.png";
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { lightTheme, darkTheme } from './themes';

const { Title } = Typography;

const StyledTitle = styled(Title)`
  font-family: "Montserrat", sans-serif;
`;

export const HeaderPage = ({ theme, setTheme }) => {
  const navigate = useNavigate();

  const StyledButton = styled(Button)`
    font-family: 'Inter', sans-serif;
    color: ${({ themeprop }) => themeprop === 'dark' ? darkTheme.color : lightTheme.color};
    border: none;
    cursor: pointer;
    background: transparent;
    &:hover {
      background: ${({ themeprop }) => themeprop === 'dark' ? '#f0f0f0' : 'rgba(0, 0, 0, 0.1)'};
      color: ${({ themeprop }) => themeprop === 'dark' ? '#222' : ''};
      text-decoration: underline;
    }
  `;

  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      height: '100%',
      background: theme === 'dark' ? darkTheme.background : lightTheme.background,
      color: theme === 'dark' ? darkTheme.color : lightTheme.color,
    }}>
      {/* Left: Logo/Image */}
      <Flex align="center" gap="10px" style={{ padding: '0 20px' }}>
        <img src={require("../images/logo.png")} alt="Logo" style={{ height: '40px' }} />
        <StyledTitle 
          level={2}
          style={{
            margin: 0,
            color: theme === 'dark' ? 'white' : 'black'
          }}
        >
          MINI SPLIT MASTER
        </StyledTitle>
      </Flex>
      {/* Right: Navigation */}
      <nav style={{ display: 'flex', alignItems: 'center', gap: '20px', fontFamily: '"Inter", sans-serif' }}>
        <Tooltip title="Toggle Dark/Light Theme">
          <Switch checked={theme === 'dark'} onChange={() => setTheme(prev => (prev === 'dark' ? 'light' : 'dark'))} />
        </Tooltip>
        <StyledButton themeprop={theme} color="default" variant="text" onClick={() => navigate("/")}>Home</StyledButton>
        <StyledButton themeprop={theme} color="default" variant="text" onClick={() => navigate("/contact")}>Contact</StyledButton>
        <StyledButton themeprop={theme} color="default" variant="text" onClick={() => navigate("/install-now")}>Install Now</StyledButton>
        <StyledButton themeprop={theme} color="default" variant="text" onClick={() => navigate("/cart")}>Cart</StyledButton>
        <StyledButton themeprop={theme} color="default" variant="text" onClick={() => navigate("/user")}> 
          <span role="img" aria-label="user" style={{ fontSize: '24px' }}>ðŸ‘¤</span>
        </StyledButton>
      </nav>
    </div>
  );
};

export const FooterPage = () => {
  return (
    <footer style={{
      padding: '10px 20px',
      background: '#cd1717ff',
      textAlign: 'center',
      height: '100%'
    }}>
      <span>Â© 2023 Your Company. All rights reserved.</span>
    </footer>
  );
};
