import { Typography } from 'antd';
import React from 'react';

const IconText = ({ icon: Icon, title, text }) => {
    const { Text, Title } = Typography;
    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            {Icon}
            <Title level={4} style={{ margin: 0, marginBottom: 4, textAlign: 'center' }}>{title}</Title>
            <Text style={{ margin: 0, textAlign: 'center' }}>{text}</Text>
        </div>
    );
}

export default IconText;
