import { useNavigate } from "react-router-dom";
import { useTheme } from "../../common/ThemeContext";
import { getTheme } from "../../config";
import { CardComponent } from "../../common/card";

const CardContact = ({ sty }) => (
  <div style={{ padding: '20px', textAlign: 'center' }}>
    <h2 style={{ color: sty.primaryColor }}>Contact Us</h2>
    <p style={{ ...sty.components.Text }}>
      We'd love to hear from you! Whether you have questions about our mini-split systems, need assistance with installation, or want to learn more about our services, our team is here to help.
    </p>
    <p style={{ ...sty.components.Text }}>
      You can reach us via email at <a href="mailto:contact@minisplit.com">contact@minisplit.com</a>
    </p>
  </div>
);

export const ContactPage = () => {
  const { theme } = useTheme();
  const currentTheme = getTheme(theme);
  const navigate = useNavigate();

  return (
    <div>
      <CardComponent
        content={<CardContact sty={currentTheme} />}
        style={{ marginTop: '10px', marginBottom: '10px' }}
      />
    </div>
  );
}
