import { Button, Col, Flex, Form, notification, Row, Steps, theme, Typography } from "antd";
import { FormikProvider, useFormik } from "formik";
import { InputFormik } from "../../common/FormikAntd/InputFormik";
import { primaryColor } from "../../config";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

const { Title, Text } = Typography;

const DistanceRoomsizeStep = () => {
  return (
    <Flex vertical style={{ justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
      <Text>Select the distance between the indoor and outdoor units:</Text>
      <InputFormik
        name="distance"
        label="Distance (in meters)"
        variant="filled"
        aria-label="distance"
        placeholder="Enter distance"
      />
    </Flex>
  );
}

const UnitsStep = () => {
  return (
    <Flex vertical style={{ justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
      <Text>Select the unit you want:</Text>
      <InputFormik
        name="unit"
        label="Unit"
        variant="filled"
        aria-label="unit"
        placeholder="Enter unit"
      />
    </Flex>
  );
}

const OthersStep = () => {
  return (
    <Flex vertical style={{ justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
      <Text>Select where the condenser will be mounted and whether it requires city permission:</Text>
      <InputFormik
        name="condenser"
        label="Condenser"
        variant="filled"
        aria-label="condenser"
        placeholder="Enter condenser details"
      />
    </Flex>
  );
}

const ScheduleStep = () => {
  return (
    <Flex vertical style={{ justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
      <Text>Schedule your appointment on the day and time that works best for you:</Text>
      <InputFormik
        name="schedule"
        label="Schedule"
        variant="filled"
        aria-label="schedule"
        placeholder="Enter schedule"
      />
    </Flex>
  );
}

const FormStep = () => {
  return (
    <Flex vertical style={{ justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
      <Text>Fill out the form with your information and make the payment:</Text>
      <InputFormik
        name="form"
        label="Form"
        variant="filled"
        aria-label="form"
        placeholder="Enter form details"
      />
    </Flex>
  );
}

const steps = [
  {
    content: (<DistanceRoomsizeStep />),
  },
  {
    content: (<UnitsStep />),
  },
  {
    content: (<OthersStep />),
  },
  {
    content: (<ScheduleStep />),
  },
  {
    content: (<FormStep />),
  },
  {
    content: 'Wait for your appointment day and enjoy the comfort!',
  },
];

export const FormInstall = ({sty, onSubmitForm}) => {
  const navigate = useNavigate();
  const [current, setCurrent] = useState(0);
  const { token } = theme.useToken();
  const next = () => {
    setCurrent(current + 1);
  };
  const prev = () => {
    setCurrent(current - 1);
  };
  const items = steps.map(item => ({ key: item.title, title: item.title }));

  const formik = useFormik({
    initialValues: {
      roomsize: '',
      distance: '',
      email: '',
      message: ''
    },
    onSubmit: values => {
      notification.success({
        message: 'Success',
        description: 'Your message has been sent successfully!',
        placement: 'topRight',
      });
      formik.resetForm();
    },
  });

  const contentStyle = {
    lineHeight: '100px',
    textAlign: 'center',
    color: token.colorTextTertiary,
    backgroundColor: token.colorFillAlter,
    borderRadius: token.borderRadiusLG,
    border: `1px dashed ${token.colorBorder}`,
    marginTop: 16,
  };

  return (
    <FormikProvider value={formik}>
      <Form layout="vertical" onFinish={formik.handleSubmit}>
        <Steps current={current} items={items} />
        <div style={contentStyle}>{steps[current].content}</div>
        <div style={{ marginTop: 24 }}>
          {current > 0 && (
            <Button style={{ margin: '0 8px' }} onClick={() => prev()}>
              Previous
            </Button>
          )}
          {current < steps.length - 1 && (
            <Button type="primary" onClick={() => next()}>
              Next
            </Button>
          )}
          {current === steps.length - 1 && (
            <Button type="primary" onClick={() => navigate('/install-now')}>
              Book your installation
            </Button>
          )}
        </div>
        <Flex vertical style={{ justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
          <Title level={3} style={{ color: primaryColor }}>Fill the form to ask about anything you want</Title>
          <Row gutter={[16, 16]} style={{ width: '80%', marginTop: 24 }}>
            <Col span={12}>
              <InputFormik
                name="name"
                label="Name"
                variant="filled"
                aria-label="name"
                placeholder="Your Name"
              />
            </Col>
            <Col span={12}>
              <InputFormik
                name="email"
                variant="filled"
                label="Email"
                type="email"
                aria-label="email"
                placeholder="Your Email"
              />
            </Col>
            <Col span={24}>
              <InputFormik.TextArea
                name="message"
                variant="filled"
                label="Message"
                aria-label="message"
                placeholder="Your Message"
              />
            </Col>
          </Row>
        </Flex>
      </Form>
    </FormikProvider>
  );
}
