import {
  DownOutlined,
  LogoutOutlined,
  MenuUnfoldOutlined,
  TagOutlined,
  UserOutlined,
} from '@ant-design/icons'
import { Button, Flex, Popover, Switch, Typography } from 'antd'
import { Header } from 'antd/es/layout/layout'
import React from 'react'
import { Link } from 'react-router-dom'
import { styled } from 'styled-components'

import { AvatarUser } from '../Common/AvatarUser'
import usecustomStyles from '../Common/customStyles'
import { useDevice } from '../Common/Hooks/useDevice.js'
import { PERMISSIONS_SECTIONS } from '../Common/Hooks/useUserPermission.js'
import { useStoredUser, useToggleUserStatus } from '../lib/authContext.js'
import RequirePermissions from '../Routes/RequirePermissions.js'

const customStyles = usecustomStyles()
const { Text } = Typography

const StyleHeader = styled(Header)`
  padding: 0.813rem 1.688rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid;
  border-color: ${({ theme }) => theme.token.colorBorder};
  z-index: 999;
  background: ${({ theme }) => theme.token.colorBgContainer};
  position: sticky;
  left: 0;
  top: 0;
  width: 100%;

  @media screen and (max-width: 768px) {
    /* Apply the responsive style without considering RTL or LTR */
    left: 0;
    right: 0;
  }
`

const HeaderContainer = styled.ul`
  display: flex;
  align-items: center;
  gap: 1.5rem;
  padding: 0;
  margin: 0;
  cursor: pointer;
  justify-content: end;
`

const StyleFlagDropdown = styled.div`
  min-width: 145px;

  ul {
    li {
      padding: 0.313rem 0;
      a {
        transition: all 0.5s ease;
        &:hover {
          color: ${customStyles.colorPrimary};
        }
      }
    }
  }
`

const HeaderLayout = ({ darkMode, handleToggleMode, collapsed, onCollapsed }) => {
  const { user } = useStoredUser()
  const { isDesktop } = useDevice()
  const { handleToggle, isActive } = useToggleUserStatus()

  const profileContentPopover = (
    <StyleFlagDropdown>
      <ul style={{ padding: '0.375rem', listStyleType: 'none' }} className="ant-pl-0 ant-mb-0">
        <li>
          <Text type="secondary" style={{ fontSize: '13px' }}>
            Bienvenido!
          </Text>
        </li>

        <li>
          <Flex align="center" gap={'0.6rem'} style={{ width: '100%' }} justify="space-between">
            <Text>Away mode</Text>
            <Switch size="small" onChange={handleToggle} checked={isActive} />
          </Flex>
        </li>

        <li>
          <Link to="/user-profile">
            <Text type="secondary">
              <UserOutlined className="ant-mr-1" size={16} />
            </Text>
            <Text> Perfil </Text>
          </Link>
        </li>

        {user.is_superadmin ? null : (
          <RequirePermissions requiredPermissions={[PERMISSIONS_SECTIONS.ticketList]}>
            <li>
              <Link to="/tickets">
                <Text type="secondary">
                  <TagOutlined className="ant-mr-1" size={16} />
                </Text>
                <Text> Tickets</Text>
              </Link>
            </li>
          </RequirePermissions>
        )}

        <li>
          <Link to="/logout">
            <Text type="secondary">
              <LogoutOutlined className="ant-mr-1" size={16} />
            </Text>
            <Text> Logout</Text>
          </Link>
        </li>
      </ul>
    </StyleFlagDropdown>
  )

  return (
    <React.Fragment>
      <StyleHeader>
        <div>
          {!isDesktop && (
            <Button type="primary" onClick={onCollapsed}>
              <MenuUnfoldOutlined />
            </Button>
          )}
        </div>

        {Object.values(user).length && (
          <HeaderContainer>
            {/* <div onClick={handleToggleMode} style={{ display: 'flex' }}>
              {darkMode === 'dark' ? <MoonOutlined size={22} /> : <SunOutlined size={22} />}
            </div> */}
            <Flex align="center" gap={'1rem'}>
              <AvatarUser src={user?.photo} size={40} hasStatus />
              <Popover placement="bottomRight" content={profileContentPopover} trigger={['click']}>
                <Flex align="center" gap={'0.6rem'}>
                  <Flex
                    vertical
                    gap={'0.188rem'}
                    justify="space-between"
                    style={{ userSelect: 'none', maxWidth: 150 }}
                  >
                    <span
                      style={{
                        fontSize: '0.875rem',
                        fontWeight: 700,
                        lineHeight: '0.9rem',
                        overflow: 'hidden',
                        whiteSpace: 'nowrap',
                        textOverflow: 'ellipsis',
                      }}
                    >
                      {user?.name}
                    </span>
                    <span
                      style={{
                        fontSize: '0.75rem',
                        overflow: 'hidden',
                        whiteSpace: 'nowrap',
                        textOverflow: 'ellipsis',
                        fontWeight: 600,
                        lineHeight: '0.8rem',
                      }}
                    >
                      {user?.edges?.role?.name}
                    </span>
                  </Flex>
                  <Button
                    size="small"
                    shape="circle"
                    icon={<DownOutlined style={{ fontSize: 10 }} />}
                  />
                </Flex>
              </Popover>
            </Flex>
          </HeaderContainer>
        )}
      </StyleHeader>
    </React.Fragment>
  )
}

export default HeaderLayout
