// import { Form, Input } from 'antd'
import { FormField, FormItem, FormLabel, FormControl, FormMessage } from '../../components/ui/form';
import { Input } from '../../components/ui/input';
import { useField } from 'formik'

export const InputFormik = ({ label, onChange, withFormItem = true, ...props }) => {
  const [field, meta] = useField({
    name: props.name,
    type: props.type || 'text',
    placeholder: props.placeholder || '',
  });

  const inputElement = (
    <FormControl>
      <Input
        {...field}
        {...props}
        onChange={e => {
          onChange?.(e);
          field.onChange(e);
        }}
      />
    </FormControl>
  );

  if (!withFormItem) {
    return (
      <FormField name={props.name}>
        {inputElement}
      </FormField>
    );
  }

  return (
    <FormField name={props.name}>
      <FormItem>
        {label && <FormLabel>{label}</FormLabel>}
        {inputElement}
        <FormMessage>{meta.touched && meta.error ? meta.error : null}</FormMessage>
      </FormItem>
    </FormField>
  );
};

const InputTextAreaFormik = ({
  label,
  onChange: _,
  withFormItem = true,
  ...props
}) => {
  const [field, meta] = useField({
    name: props.name,
    type: props.type || 'text',
    placeholder: props.placeholder || '',
  });

  const inputElement = (
    <FormControl>
      <Input.TextArea
        {...field}
        {...props}
      />
    </FormControl>
  );

  if (!withFormItem) {
    return (
      <FormField name={props.name}>
        {inputElement}
      </FormField>
    );
  }

  return (
    <FormField name={props.name}>
      <FormItem>
        {label && <FormLabel>{label}</FormLabel>}
        {inputElement}
        <FormMessage>{meta.touched && meta.error ? meta.error : null}</FormMessage>
      </FormItem>
    </FormField>
  );
};

const InputPasswordFormik = ({ label, onChange: _, withFormItem = true, ...props }) => {
  const [field, meta] = useField({
    name: props.name,
    type: 'password',
    placeholder: props.placeholder || '',
  });

  const inputElement = (
    <FormControl>
      <Input.Password
        {...field}
        {...props}
      />
    </FormControl>
  );

  if (!withFormItem) {
    return (
      <FormField name={props.name}>
        {inputElement}
      </FormField>
    );
  }

  return (
    <FormField name={props.name}>
      <FormItem>
        {label && <FormLabel>{label}</FormLabel>}
        {inputElement}
        <FormMessage>{meta.touched && meta.error ? meta.error : null}</FormMessage>
      </FormItem>
    </FormField>
  );
};

InputFormik.Password = InputPasswordFormik
InputFormik.TextArea = InputTextAreaFormik
