import { Col, Form, Input, Slider } from 'antd'
import { useField } from 'formik'

export const SliderFormik = ({ label, onChange, size = 'large', seeNumber, style, ...props }) => {
  const [field, meta] = useField({
    name: props.name,
    placeholder: props.placeholder || '',
  })

  return (
    <Form.Item
      validateStatus={meta.touched && meta.error ? 'error' : ''}
      help={meta.touched && meta.error ? meta.error : undefined}
      label={label}
    >
      {seeNumber && field.value !== undefined ?
      <Col style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: 6 }}>
          <Input value={field.value}
            variant='underlined'
            style={{ justifyContent: 'center', textAlign: 'center', marginBottom: 6 }}
            onChange={e => {
            const value = Number(e.target.value);
            if (!isNaN(value)) {
              onChange?.(value)
              field.onChange({
                target: { value, name: props.name },
              })
            }
          }}/>
      </Col>
        : null}
      <Slider
        size={size}
        style={{ ...style }}
        {...field}
        value={field.value || undefined}
        {...props}
        onChange={value => {
          onChange?.(value)
          field.onChange({
            target: { value, name: props.name },
          })
        }}
      />
    </Form.Item>
  )
}
