import { Button, Flex, Typography } from "antd";
import { CardComponent } from "../../common/card";
import { useTheme } from "../../common/ThemeContext";
import { getTheme } from "../../config";
import bgPage from "../../images/bgPage.png";


export const HomePage = () => {
  const { theme } = useTheme();
  const currentTheme = getTheme(theme);

  return (
    <div>
      <div style={{ position: 'relative', width: '100%' }}>
        <img src={bgPage} alt="ChatGPT" style={{ width: "100%", maxHeight: "85vh", objectFit: "cover", display: 'block' }} />
        <Flex vertical style={{
          position: 'absolute',
          top: '20%',
          left: '50%',
          transform: 'translate(-50%, -20%)',
          textAlign: 'center',
          gap: '10px',
          width: '100%',
          pointerEvents: 'none',
        }}>
          <div style={{ pointerEvents: 'auto', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '10px' }}>
            <Typography.Text>Ready for a Comfort Upgrade?</Typography.Text>
            <Typography.Text>Get Your Mini Split Installed Today!</Typography.Text>
            <Typography.Text>All equipment includes installation</Typography.Text>
            <Button type="primary" style={{ marginTop: '10px' }}>Click Me</Button>
          </div>
        </Flex>
      </div>
      <CardComponent
        theme={{
          borderColor: currentTheme.token.borderColorCard,
          background: currentTheme.token.backgroundCard,
          color: currentTheme.token.colorText,
        }}
        content={<p>This is the home page content.</p>}
        style={{ marginTop: '10px' }}
      />
    </div>
  );
};
