import { ConfigProvider, Layout } from 'antd'
import React, { useState } from 'react'
import { ThemeProvider } from 'styled-components'
import { darkthemecolors, themecolor } from '../../src/config'
import { useTheme } from '../Common/ThemeContext'
import withRouter from '../Common/withRouter'
import FooterLayout from './Footer'
import HeaderLayout from './Header'
import { Sidebar } from './Sidebar'

const { Content } = Layout

const LayoutComponents = ({ children }) => {
  const [collapsed, setCollapsed] = useState(true)

  const { theme, toggleTheme } = useTheme()

  const toggleCollapsed = () => {
    setCollapsed(!collapsed)
  }

  const handleToggleMode = () => {
    toggleTheme()
  }

  return (
    <React.Fragment>
      <ThemeProvider theme={theme === 'dark' ? darkthemecolors : themecolor}>
        <ConfigProvider theme={theme === 'dark' ? darkthemecolors : themecolor}>
          <Layout>
            <Sidebar
              collapsed={collapsed}
              onClickCollapse={() => {
                toggleCollapsed()
              }}
            />
            <Layout id="main-layout" style={{ minHeight: '100vh' }}>
              <HeaderLayout
                collapsed={collapsed}
                onCollapsed={() => {
                  toggleCollapsed()
                }}
                darkMode={theme}
                handleToggleMode={handleToggleMode}
              />
              <Content>
                <div
                  style={{
                    padding: '1rem',
                  }}
                >
                  {children}
                </div>
              </Content>
              <FooterLayout />
            </Layout>
          </Layout>
        </ConfigProvider>
      </ThemeProvider>
    </React.Fragment>
  )
}

export default withRouter(LayoutComponents)
