import styled from 'styled-components'
import { primaryColor, themecolor } from '../config'
import { Menu, Dropdown } from 'antd'

export const AsideWrapper = styled.div`
  position: sticky;
  height: 100vh;
  left: 0;
  top: 0;
`

export const TriggerAsideShadowStyled = styled.div`
  width: 0px;
  height: 66px;
  border-top: 8px solid transparent;
  border-bottom: 8px solid transparent;
  border-left: 14px solid rgba(0, 0, 0, 0.1);
  border-right: none;
  position: absolute;
  z-index: ${themecolor.token.zIndexPopupBase - 3};
  opacity: 0.5;
  filter: blur(2px);
  left: 100%;
  top: 50%;
  transform: translateY(-50%);
`

export const TriggerAsideStyled = styled.button`
  position: absolute;
  background: transparent;
  border: none;
  left: calc(100% - 2px);
  top: 50%;
  transform: translateY(-50%);
  cursor: pointer;
  z-index: ${themecolor.token.zIndexPopupBase - 3};
  width: 0px;
  height: 66px;
  border-top: 8px solid transparent;
  border-bottom: 8px solid transparent;
  border-left: 14px solid
    ${({ theme }) => {
      return theme.token.colorBgContainer
    }};
  border-right: 'none';
`

export const MenuStyled = styled(Menu)`
  .ant-menu-item-group {
    padding: 1rem 0;
    border-bottom: 1px solid #e2e8f0;
  }
  .ant-menu-item-group-title {
    display: ${props => (props.$collapsed ? 'none' : 'block')};
  }
`
export const DropdownStyled = styled(Dropdown)`
  &:hover {
    border: 1px solid ${primaryColor};
    transition: border 0.2s;
    & .category {
      color: ${primaryColor};
    }
  }
`
